import stepOne from '../../assets/icons/icon_step_one.svg';
import stepTwo from '../../assets/icons/icon_step_two.svg';
import stepThree from '../../assets/icons/icon_step_three.svg';

const steps = [
    {
        img: stepOne,
        title: "Choose Bouquet",
        description: "Explore our collection and pick the perfect arrangement for any occasion."
    }, {
        img: stepTwo,
        title: "Contact to Order",
        description: "Call or message us to place your order and add thoughtful personal touches.",
    }, {
        img: stepThree,
        title: "We Deliver",
        description: "Handcrafted with care and delivered fresh, right on schedule – every time."
    }]

export default steps;
