import * as yup from 'yup';
import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import axios from "axios";
import {useState} from "react";
import styles from "./RegisterPage.module.scss";

const schema = yup.object({
    name: yup.string().required("Name is required"),
    email: yup.string().email("Invalid email format").min(10, "Min 10 characters").required("Email is required"),
    password: yup.string().min(8, "Min 8 characters").required("Password is required"),
    confirmPassword: yup.string().oneOf([yup.ref("password"),"Password does not match"]).required("Password confirm your password"),
})

const RegisterPage = () => {
    const [isHidden, setIsHidden] = useState(false);

    const {register, formState: {errors}, reset, handleSubmit} = useForm({
            resolver: yupResolver(schema)
        }
    );

    const onSubmit = async (values) => {
        try {
            const res = await axios.post("http://localhost:5050/api/auth/register", values);

            alert("Successfully registered!");
            reset();
        } catch (e) {
            console.log("ERROR:", e.response?.data || e.message);
            alert("Failed to register");
        }
    }


    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-group">
                <div>
                    <input type="text" placeholder="Your full name" autoComplete="name" {...register("name")}/>
                    <p>{errors.name?.message}</p>
                </div>
                <div>
                    <input type="email" placeholder="Your email address" autoComplete="email" {...register("email")}/>
                    <p>{errors.email?.message}</p>
                </div>
                <div>
                    <input type={isHidden ? "password" : "text"} autoComplete="new-password"
                           placeholder="Your password" {...register("password")}/>
                    <button onClick={() => setIsHidden(!isHidden)} type="button">{isHidden ? "üëÅÔ∏è" : "üôà"}</button>
                    <p>{errors.password?.message}</p>
                </div>
                <div>
                    <input type="password" placeholder="Your password"
                           autoComplete="new-password" {...register("confirmPassword")}/>
                    <p>{errors.confirmPassword?.message}</p>
                </div>
                <div>
                    <button type="submit">Register</button>
                </div>
            </div>
        </form>
    )
}
export default RegisterPage;
