import {useState} from "react";
import styles from "./QuickOrderForm.module.scss";

export const QuickOrderForm = ({onSuccess}) =>{
    const [phone, setPhone] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!phone.trim() || !phone.trim().length) {
            setError("Enter your phone");
            return;
        }
        setError(null);
        setLoading(true);

        try {
            const res = await fetch(`/api/order/quick-order`, {
                method: 'POST',
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify({phone}),
            });
            if (!res.ok) throw new Error("Failed to save quick order");
            setLoading(false);
            setPhone("");
            if (onSuccess) onSuccess();
            alert("Successfully added quick order");
        } catch (err) {
            console.log(err);
            setError(err);
            setLoading(false);
        }
    }
    return(
        <form className={styles.formTel} onSubmit={handleSubmit}>
            <input className={styles.inputTel} pattern="\+?380{9}$" onChange={e => setPhone(e.target.value)} value={phone} type="text" placeholder="Phone" />
            <button className={styles.btnTel} type="submit">Quick order</button>
        </form>
    )
}
