import {createSlice} from "@reduxjs/toolkit";

const initialState = {
    items: JSON.parse(localStorage.getItem("cartItems")) || [],
    totalPrice: JSON.parse(localStorage.getItem("totalPrice"))|| 0,
    totalCount: JSON.parse(localStorage.getItem("totalCount"))|| 0,
};

const saveToLocalStorage = (state) => {
    localStorage.setItem("cartItems", JSON.stringify(state.items));
    localStorage.setItem("totalPrice", JSON.stringify(state.totalPrice));
    localStorage.setItem("totalCount", JSON.stringify(state.totalCount));
}

const calculateTotalPrice = (state) => {
    state.totalCount = state.items.reduce((total, item) => total+item.quantity, 0);
    state.totalPrice = state.items.reduce((total, item) => total+item.quantity * item.price, 0);
}

const cartSlice = createSlice({
    name: "cart",
    initialState,
    reducers: {
        addToCart: (state, action) => {
            const item = action.payload;
            const existing = state.items.find((i) => i.id === item.id);

            if (existing) {
                existing.quantity += 1;
            } else {
                state.items.push({...item, quantity: 1});
            }
            saveToLocalStorage(state);
            calculateTotalPrice(state);
        },
        removeFromCart: (state, action) => {
            state.items = state.items.filter((i) => i.id !== action.payload);
            saveToLocalStorage(state);
            calculateTotalPrice(state);

        },
        decreaseQuantity: (state, action) => {
            const item = state.items.find((i) => i.id === action.payload);
            if (item) {
                if (item.quantity >= 1) {
                    item.quantity -= 1;
                } else {
                    state.items = state.items.filter((i) => i.id !== action.payload);
                }
                saveToLocalStorage(state);
                calculateTotalPrice(state);

            }
        },
        clearCart: (state, action) => {
            state.items = [];
            saveToLocalStorage(state);
            calculateTotalPrice(state);
        },

    }
})

export const {addToCart, decreaseQuantity, removeFromCart, clearCart} = cartSlice.actions;
export default cartSlice.reducer;
