import PageHeader from "../pageHeader/PageHeader";
import styles from "./ProfilePage.module.scss";
import {useSelector} from "react-redux";
import {useState} from "react";
import axios from "axios";


const ProfilePage = () => {
    const user = useSelector(state => state.auth.user);
    const [edit, setEdit] = useState(false);
    const [formData, setFormData] = useState({
        name: user?.name || "",
        email: user?.email || "",
        phone: user?.phone || "",
        city: user?.city || "",
    })

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        })
    }

    const handleSave = async(e) => {
        e.preventDefault();
        try{
            const res = await axios.put('http://localhost:5050/api/auth/update', {
                id: user.id,
                name: formData.name,
                email: formData.email,
                phone: formData.phone || null,
                city: formData.city || null
            });
            console.log("Saving profile:", res.data);
            setUser(res.data);
            setEdit(false);
        }catch(error){
            console.error("Error updating user:", error.response?.data || error.message);
        }

    }

    return (
        <div className={styles.profilePage}>
            <PageHeader style={{background: "#205529", color: "white"}} label="Profile"
                        subtitle="Profile "/>
            <div className={styles.profileInfo}>
                <button className={styles.edit} onClick={()=>setEdit(!edit)}> üñãÔ∏è</button>
                {edit ? (
                    <div>
                        <input type="text" name="name" placeholder="name" value={formData.name} onChange={handleChange} />
                        <input type="email" name="email" placeholder="email" value={formData.email} onChange={handleChange} />
                        <input type="phone" name="phone" placeholder="phone" value={formData.phone} onChange={handleChange} />
                        <input type="text" name="city" placeholder="city" value={formData.city} onChange={handleChange}/>
                        <button type="button" onClick={handleSave}>SAVE</button>
                    </div>

                ) : (
                    <div className={styles.containerInfo}>
                        <p><strong>Name : </strong> {user?.name}</p>
                        <p><strong>Email : </strong> {user?.email}</p>
                        <p><strong>Phone : </strong>{user?.phone}</p>
                        <p><strong>City : </strong>{user?.city}</p>
                    </div>
                )
                }


            </div>
        </div>
    )
}

export default ProfilePage;
