import styles from './TabPanel.module.scss';
import {useState} from "react";
import features from "./features";

const TabPanel = ({product}) => {
    const [activeTab, setActiveTab] = useState("description");

    return (
        <div className={styles.tabPanel}>
            <div className={styles.tabs}>
                <button className={activeTab === "description" ? "tab active" : "tab"}
                        onClick={() => setActiveTab("description")}>
                    DESCRIPTION
                </button>
                <button className={activeTab === "delivery" ? "tab active" : "tab"}
                        onClick={() => setActiveTab("delivery")}>
                    DELIVERY
                </button>
                <button className={activeTab === "payment" ? "tab active" : "tab"}
                        onClick={() => setActiveTab("payment")}>
                    PAYMENT
                </button>
            </div>
            <div className={styles.tabContent}>
                {activeTab === "description" && (
                    <div className={styles.descriptionTab}>
                        <p>{product.description}</p>
                        <div className={styles.featuresGrid}>
                            {features.map((feature, index) => (
                                    <div className={styles.feature} key={index}>
                                        <img src={feature.icon} alt={feature.label}/>
                                        <p>{feature.label}</p>
                                    </div>
                                ))}
                        </div>
                    </div>

                )}
                {activeTab === "delivery" && (
                    <p>delivery</p>
                )}
                {activeTab === "payment" && (
                    <p>payment</p>
                )}
            </div>
        </div>
    )
}
export default TabPanel;
