import {Link} from "react-router-dom";
import styles from "./ProductsCard.module.scss";
import {useDispatch, useSelector} from "react-redux";
import {addToCart} from "../../store/cartSlice";
import iconLike from "../../assets/icons/icon_like.png";
import iconLikeTrue from "../../assets/icons/icon_like_true.png";
import {addFavorite, deleteFavorite} from "../../store/favoriteSlice";

const ProductsCard = ({product}) => {
    const dispatch = useDispatch();

    const favorites = useSelector(state => state.favorites.favorites);
    const isFavorite = favorites.some((f) => f.id === product.id);

    const addToCartHandler = () => {
        dispatch(addToCart(product))
    };

    const addFavouriteHandler = () => {
        dispatch(addFavorite(product))
    }

    const deleteFavouriteHandler = () => {
        dispatch(deleteFavorite(product))
    }

    return (
        <section className={styles.allFlowers}>
            <div className={styles.flower}>
                <Link to={`/products/${product.id}`}>
                    <img src={`http://localhost:5050${product.image}`}/>
                </Link>
                <div className={styles.flowerInfo}>
                    <Link to={`/products/${product.id}`}>{product.title}</Link>
                </div>
                <div className={styles.addToCart}>
                    {
                        product.discountPercent > 0 ? (
                            <div>
                                <span style={{textDecoration: "line-through", color: "gray"}}>${product.price}</span>
                                <span style={{marginLeft: "8px", fontWeight: "bold"}}> ${product.finalPrice}</span>
                                <span style={{marginLeft: "8px", color: "red"}}>- {product.discountPercent}%</span>
                            </div>
                        ) : (
                            <span>${product.price}</span>
                        )
                    }
                    <div className={styles.icons}>
                        {isFavorite ? (
                                <button className={styles.btnFavorites} onClick={deleteFavouriteHandler}>
                                    <img style={{width: "30px"}} src={iconLikeTrue} alt="like"/>
                                </button>
                            ): (
                                <button className={styles.btnFavorites} onClick={addFavouriteHandler}>
                                    <img style={{width: "30px"}} src={iconLike} alt="like"/>
                                </button>
                            )
                        }

                    </div>
                    <button onClick={addToCartHandler} className={styles.btnAddToCart}>+</button>
                </div>
            </div>
        </section>
    )
}
export default ProductsCard;
