import PageHeader from "../pageHeader/PageHeader";
import styles from "./ProfilePage.module.scss";
import {useDispatch, useSelector} from "react-redux";
import {useEffect, useState} from "react";
import axios from "axios";
import {setUser} from "../../store/authSlice";


const ProfilePage = () => {
    const dispatch = useDispatch();
    const user = useSelector(state => state.auth.user);
    const [edit, setEdit] = useState(false);

    const [formData, setFormData] = useState({
        name: user?.name || "",
        email: user?.email || "",
        phone: user?.phone || "",
        city: user?.city || "",
        date: user?.date || "",
        family: user?.family || "",
    })

    useEffect(() => {
        setFormData({
            name: user?.name || "",
            email: user?.email || "",
            phone: user?.phone || "",
            city: user?.city || "Kyiv",
            date: user?.date || "",
            family: user?.family || "",
        });
    }, [user])

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        })
    }

    const handleSave = async (e) => {
        e.preventDefault();

        try {
            const res = await axios.put('http://localhost:5050/api/auth/update', {
                id: user.id,
                name: formData.name,
                email: formData.email,
                phone: formData.phone || null,
                city: formData.city || null,
                date: formData.date || null,
                family: formData.family || null,
            });
            console.log("DATA FROM SERVER:", res.data);
            dispatch(setUser(res.data.user));
            setEdit(false);


        } catch (error) {
            console.error("Error updating user:", error.response?.data || error.message);
        }
    }

    return (
        <div className={styles.profilePage}>
            <PageHeader style={{background: "#205529", color: "white"}} label="Profile"
                        subtitle="Profile user info"/>
            <div className={styles.profileInfo}>
                    <div className={styles.btnEdit}>
                        <button className={styles.edit} onClick={() => setEdit(!edit)}> üñãÔ∏è</button>
                    </div>

                <h3>User Info</h3>
                {edit ? (
                    <div>
                        <p><strong>Name : </strong></p>
                        <input type="text" name="name" placeholder="name" value={formData.name}
                               onChange={handleChange}/>
                        <p><strong>Family: </strong></p>
                        <input type="text" name="family" placeholder="family" value={formData.family}
                               onChange={handleChange}/>
                        <p><strong>Email : </strong></p>
                        <input type="email" name="email" placeholder="email" value={formData.email}
                               onChange={handleChange}/>
                        <p><strong>Phone : </strong></p>
                        <input type="phone" name="phone" placeholder="phone" value={formData.phone}
                               onChange={handleChange}/>
                        <p><strong>City : </strong></p>
                        <select name="city" value={formData.city} onChange={handleChange}>
                            <option value="Kyiv">Kyiv</option>
                            <option value="Kharkiv">Kharkiv</option>
                            <option value="Lviv">Lviv</option>
                            <option value="Poltava">Poltava</option>
                            <option value="Odesa">Odesa</option>
                        </select>
                        <p><strong>Date birthday : </strong></p>
                        <input type="date" name="date" value={formData.date} onChange={handleChange}/>
                        <button type="button" onClick={handleSave}>SAVE</button>
                    </div>
                ) : (
                    <div className={styles.containerInfo}>
                    <p><strong>Name : </strong> {user?.name}</p>
                        <p><strong>Family: </strong>{user?.family}</p>
                        <p><strong>Email : </strong> {user?.email}</p>
                        <p><strong>Phone : </strong>{user?.phone}</p>
                        <p><strong>City : </strong>{user?.city}</p>
                        <p><strong>Date birthday : </strong>{user?.date?.slice(0, 10)}</p>
                    </div>
                )}
            </div>
            <div className={styles.profileOrder}>

            </div>
        </div>
    )
}

export default ProfilePage;
