import styles from './OrderForm.module.scss';
import * as yup from "yup";
import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";

const schema = yup.object().shape({
    name: yup.string().required(),
    phone: yup.string().required().matches(/^\d{10}$/),
    family: yup.string(),
    email: yup.string().email().required(),
    city: yup.string().required(),
})

const OrderForm = () => {
    const {register, handleSubmit, formState: {errors}, reset} = useForm({
        resolver: yupResolver(schema),
    })

    const onSubmit = (values) => {
        console.log(values);
        alert("+++");
        reset();
    }
    return (
        <div className={styles.orderForm}>
            <div className={styles.dataUser}>
                <span>Your data</span>
                <p>Log in to the website and we will save all your order information and automatically fill in your
                    contact information.</p>
            </div>
            <div className={styles.btnIsAuth}>
                <button>Log In</button>
                <button></button>
            </div>
            <div className={styles.form}>
                <form id="orderForm" onSubmit={handleSubmit(onSubmit)}>
                    <div>
                        <input type="tel"  {...register("phone")}/>
                        {errors.phone && <p>{errors.phone.message}</p>}
                    </div>
                    <div>
                        <input type="text"  {...register("family")}/>
                        {errors.family && <p>{errors.family.message}</p>}
                    </div>
                    <div>
                        <input type="email"  {...register("email")}/>
                        {errors.email && <p>{errors.email.message}</p>}
                    </div>
                    <div>
                        <input type="name"  {...register("name")}/>
                        {errors.name && <p>{errors.name.message}</p>}
                    </div>
                    <div>
                        <select>
                            <option>Kyiv</option>
                            <option>Kharkiv</option>
                            <option>Lviv</option>
                            <option>Poltava</option>
                            <option>Odesa</option>
                        </select>
                    </div>
                </form>
            </div>
            <div className={styles.dataUser}>
                <span>Delivery</span>
                <input type="radio"/>
                Check the address with the recipient
            </div>
        </div>
    )
}
export default OrderForm;
