import {useDispatch, useSelector} from "react-redux";
import styles from "../CartPage.module.scss";
import {clearCart, decreaseQuantity} from "../../../store/cartSlice";
import garbage from "../../../assets/icons/icon_garbage.png";
import garbageOpen from "../../../assets/icons/icon_garbage_open.png";
import {useState} from "react";

const CartList = () => {
    const {items} = useSelector((state) => state.cart);
    const dispatch = useDispatch();

    const [isHovered, setIsHovered] = useState(false);

    const total = items.reduce((sum, item) => sum + item.price * item.quantity, 0);

    if(items.length === 0){
        return <h2>–ö–æ—Ä–∑–∏–Ω–∞ –ø—É—Å—Ç–∞ üòî</h2>
    }
    return(
        <div className="cartList">
            {
                items.map((i) => (
                    <div key={i.id}>
                        <div className={styles.name}>
                            <img src={i.image} alt={i.title}/>
                            <p>{i.title}</p>
                        </div>
                        <div className={styles.price}>
                            <p>{i.price}</p>
                        </div>
                        <div className={styles.qty}>
                            <button onClick={dispatch(decreaseQuantity(i.id))} className={styles.minus}>-</button>
                            <p>{i.quantity}</p>
                            <button className={styles.plus}>+</button>
                        </div>
                        <div className={styles.sum}>
                            <p>{total}</p>
                        </div>
                        <div className={styles.btnClear}
                             onMouseEnter={() => setIsHovered(true)}
                             onMouseLeave={() => setIsHovered(false)}
                        >

                            <button onClick={dispatch(clearCart)}>{isHovered ? <img src={garbageOpen}/> : <img src={garbage}/>}</button>
                        </div>
                    </div>
                ))
            }
        </div>
    )
}
export default CartList;
