import styles from './HomePage.module.scss';
import flowerOne from '../../assets/images/img_illustration_flower_one.png';
import flowerTwo from '../../assets/images/img_illustration_flower_two.png';
import {useNavigate} from "react-router-dom";
import SpecialOffer from "../specialOffer/SpecialOffer";
import Customers from "./customers/Customers";
import AboutLaFlora from "./aboutLaFlora/AboutLaFlora";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {fetchProductsByCategory} from "../../store/productsSlice";
import Products from "../../components/products/Products";

const HomePage = ({category}) => {
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const products = useSelector(state => state.products.filtered.items);
    console.log(products);

    useEffect(()=>{
        if(category){
            dispatch(fetchProductsByCategory(category))
        }

    },[dispatch, category])

    return (
        <div className={styles.home}>
            <section className={styles.homeBackground}>
                <div className={styles.imgFlowerLeft}>
                    <img src={flowerOne} alt="Flower"/>
                </div>
                <div className={styles.content}>
                    <div className={styles.flag}>3-Hour Delivery</div>
                    <h1>Elegant Bouquets, <br/> Locally Crafted in LA</h1>
                    <button onClick={()=>navigate('/products')}>Browse Bouquets</button>
                </div>
                <div className={styles.imgFlowerRight}>
                    <img src={flowerTwo} alt="Flower"/>
                </div>
            </section>
             <section>
                 <div className={styles.flowerBlock}>
                     <p>New Arrivals</p>
                     <button>All Bouquets > </button>
                 </div>
                 <div>
                     {<Products category={1} />}
                     }
                 </div>
             </section>
            <section>
                <div className={styles.flowerBlock}>
                    <p>Client’s Choice</p>
                    <button>All Bouquets > </button>
                </div>
                <div>

                </div>
            </section>
            <SpecialOffer/>
            <section>
                <div className={styles.flowerBlock}>
                    <p>On Sale</p>
                    <button>On Sale ></button>
                </div>
            </section>
            <Customers/>
            <AboutLaFlora/>
        </div>
    )
}
export default HomePage;
