import styles from './OrderForm.module.scss';
import * as yup from "yup";
import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import {useSelector} from "react-redux";
import axios from "axios";

const schema = yup.object().shape({
    name: yup.string().required(),
    phone: yup.string().required().matches(/^\d{10}$/),
    family: yup.string(),
    email: yup.string().email().required(),
    city: yup.string().required(),
    phoneRecipient: yup.string(),
})

const OrderForm = () => {

    const totalCount = useSelector(state => state.cart.totalCount)
    const totalPrice = useSelector(state => state.cart.totalPrice)

    const {register, handleSubmit, formState: {errors}, reset, watch} = useForm({
        defaultValues: {
            agree:false,
            city: "Kyiv"
        },
        resolver: yupResolver(schema),
    })

    const onSubmit = async (values) => {
try{
    const res = await axios.post("http://localhost:5000/api/order", values)
    alert("Order sent!");
    reset();
} catch (e){
    console.log(e);
    alert("Error sending order")
}


    }

    return (
        <div className={styles.orderForm}>
            <div className={styles.dataUser}>
                <span>Your data</span>
                <p>Log in to the website and we will save all your order information and automatically fill in your
                    contact information.</p>
            </div>
            <div className={styles.btnIsAuth}>
                <button>Log In</button>
                <button></button>
            </div>
            <div className={styles.form}>
                <form id="orderForm" onSubmit={handleSubmit(onSubmit)}>
                    <div>
                        <input type="tel"  {...register("phone")} placeholder="phone"/>
                        {errors.phone && <p>{errors.phone.message}</p>}
                    </div>
                    <div>
                        <input type="name"  {...register("name")} placeholder="name"/>
                        {errors.name && <p>{errors.name.message}</p>}
                    </div>
                    <div>
                        <input type="text"  {...register("family")} placeholder="family"/>
                        {errors.family && <p>{errors.family.message}</p>}
                    </div>
                    <div>
                        <input type="email"  {...register("email")} placeholder="email"/>
                        {errors.email && <p>{errors.email.message}</p>}
                    </div>
                    <div>
                        <select name="city">
                            <option value="Kyiv">Kyiv</option>
                            <option value="Kharkiv">Kharkiv</option>
                            <option value="Lviv">Lviv</option>
                            <option value="Poltava">Poltava</option>
                            <option value="Odesa">Odesa</option>
                        </select>
                    </div>
                </form>
            </div>
            <div className={styles.dataUser}>
                <span>Delivery</span>
                <input type="checkbox" {...register("agree")}/>
                Check the address with the recipient
                <div className={styles.recipient}>
                    <input type="tel" {...register("phoneRecipient")}/>
                </div>
                <div className={styles.adressRecipient}>
                    <input type="text" {...register("adress")}/>
                </div>
            </div>
            <div className={styles.record}>
                <span>Record/gift</span>
                <textarea placeholder="Your greeting:" rows="10" cols="60"></textarea>
            </div>
            <div className={styles.orderResults}>
                <div>
                    <div className={styles.total}>
                        <p>{totalCount} goods worth:</p>

                        <p>${totalPrice}</p>
                    </div>
                    <div className={styles.total}>
                        <p style={{fontWeight: "800", color: "#205529"}}>Total:</p>
                        <p style={{fontWeight: "800", color: "#205529"}}>${totalPrice}</p>
                    </div>
                    <button>Place an order</button>
                </div>
            </div>
        </div>
    )
}
export default OrderForm;
