import tel from "../../../assets/icons/icon_tel.svg";
import openMenu from "../../../assets/icons/icon_open_menu.svg";
import closeMenu from "../../../assets/icons/icon_close_menu.svg";
import styles from "./HeaderTablet.module.scss";
import {useState} from "react";
import MobileMenu from "../mobileMenu/MobileMenu";
import {Link} from "react-router-dom";
import {iconCart} from "../../assets/icons/icon_cart.png";

const HeaderTablet = () => {
    const [onOpen, setOpen] = useState(false);

    return (
        <div className={styles.headerTablet}>
            <div className={styles.logo}>
                <Link to="/"><p>La Flora <br/>Boutique</p></Link>
            </div>
            <div className={styles.navBar}>
                <div className={styles.telephone}>
                    <img src={tel} alt="tel:"/>
                    <p>+1 (213) 555-0198</p>
                </div>
                <div className={styles.iconCart}>
                    <img src={iconCart} alt="cart"/>
                </div>
                <div className={styles.mobileMenu}>
                    {!onOpen ? (
                        <button onClick={() => setOpen(true)}><img src={openMenu} alt="=="/></button>
                    ) : (
                        <button onClick={() => setOpen(false)}><img src={closeMenu} alt="X"/></button>
                    )}
                </div>
                <MobileMenu isOpen={onOpen} isClose={()=>setOpen(false)} />
            </div>
        </div>
    )
}
export default HeaderTablet;
