import * as yup from 'yup';
import {useForm} from "react-hook-form";
import {yupResolver} from "@hookform/resolvers/yup";
import axios from "axios";

const schema = yup.object({
    name: yup.string().required(),
    email: yup.string().email().required(),
    password: yup.string().required(),
    passwordExact: yup.string().oneOf([yup.ref("password")]).required(),
})

const RegisterPage = () => {

    const {register, formState: {errors}, reset, handleSubmit} = useForm({
            resolver: yupResolver(schema)
        }
    );

    const onSubmit = async (values) => {
        try {
            const res = await axios.post("http://localhost:5050/api/auth/register", values);
            if (res.status !== 200) {
                throw new Error("Failed to register");
            }

            alert("Successfully registered!");
            reset();

        } catch (e) {
            console.log("ERROR:", e.response?.data || e.message);
            alert("Failed to register");
        }
    }


    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="form-group">
                <div>
                    <input type="text" placeholder="Your full name" {...register("name")}/>
                    <p>{errors.name?.message}</p>
                </div>
                <div>
                    <input type="email" placeholder="Your email address" {...register("email")}/>
                    <p>{errors.email?.message}</p>
                </div>
                <div>
                    <input type="password" placeholder="Your password" {...register("password")}/>
                    <p>{errors.password?.message}</p>
                </div>
                <div>
                    <input type="passwordExact" placeholder="Your password" {...register("passwordExact")}/>
                    <p>{errors.passwordExact?.message}</p>
                </div>
                <div>
                    <button type="submit">Register</button>
                </div>
            </div>
        </form>
    )
}
export default RegisterPage;
