import axios from "axios";
import {createAsyncThunk, createSlice} from "@reduxjs/toolkit";

export const fetchAllProducts = createAsyncThunk(
    'products/fetchAllProducts',
    async (_, {rejectWithValue}) => {
        try {
            const response = await axios.get("http://localhost:5050/api/products");
            return response.data;

        } catch (error) {
            return rejectWithValue(error.response?.data?.message || error.message);
        }
    }
)

export const fetchProductById = createAsyncThunk(
    'products/fetchProductById',
    async (id, {rejectWithValue}) => {
        try {
            const response = await axios.get(`http://localhost:5050/api/products/${id}`);
            return response.data;
        } catch (error) {
            return rejectWithValue(error.response?.data?.message || error.message);
        }
    }
)

export const fetchProductsByCategory = createAsyncThunk(
    'products/fetchProductsByCategory',
    async (categoryId, {rejectWithValue}) => {
        try {
            const response = await axios.get(`http://localhost:5050/api/products/category/${categoryId}`);
            return {categoryId, data:response.data};
        } catch (error) {
            return rejectWithValue(error.response?.data?.message || error.message);
        }
    }
)

const productsSlice = createSlice({
        name: "products",
        initialState: {
            all: {
                items: [],
                status: "idle",
                error: null
            },
            selected: {
                item: null,
                status: "idle",
                error: null
            },
            filtered: {
                itemsByCategory: {},
                status: "idle",
                error: null
            }
        },
        reducers: {},
        extraReducers: (builder) => {
            builder
                .addCase(fetchAllProducts.pending, (state) => {
                    state.all.status = 'loading';
                })

                .addCase(fetchAllProducts.fulfilled, (state, action) => {
                    state.all.status = 'succeeded';
                    state.all.items = action.payload;
                })
                .addCase(fetchAllProducts.rejected, (state, action) => {
                    state.all.status = 'failed';
                    state.all.error = action.payload || action.error.message;
                })
                .addCase(fetchProductById.pending, (state) => {
                    state.selected.status = 'loading';
                })
                .addCase(fetchProductById.fulfilled, (state, action) => {
                    state.selected.status = 'succeeded';
                    state.selected.item = action.payload;
                })
                .addCase(fetchProductById.rejected, (state, action) => {
                    state.selected.status = 'failed';
                    state.selected.error = action.payload || action.error.message;
                })
                .addCase(fetchProductsByCategory.pending, (state, action) => {
                    state.filtered.status = 'loading';
                })
                .addCase(fetchProductsByCategory.fulfilled, (state, action) => {
                    state.filtered.status = 'succeeded';
                    const {categoryId, data} = action.payload;
                    state.filtered.itemsByCategory[categoryId] = data;

                })
                .addCase(fetchProductsByCategory.rejected, (state, action) => {
                    state.filtered.status = 'failed';
                    state.filtered.error = action.payload || action.error.message;
                })

        }
    }
)

export default productsSlice.reducer;
