import styles from "./FavoritesPage.module.scss";
import {useSelector} from "react-redux";
import ProductsCard from "../../components/products/ProductsCard";
import PageHeader from "../pageHeader/PageHeader";

const FavoritesPage = () => {
    const favorites = useSelector((state) => state.favorites.favorites);

    return (
        <div className={styles.favoritesPage}>
            <PageHeader label="Favorites" subtitle="Explore our complete collection of handcrafted bouquets." />

            <div className={styles.favoritesFlowers}>
                {favorites.length === 0 ? (
                    <div className={styles.notFavorites}>
                        <p>You don't have any favorite flowers üíê</p>
                    </div>
                ) : (
                    <div className={styles.favoriteFlowersBlock}>
                        {
                            favorites.map(fav => (
                                <ProductsCard key={fav.id} product={fav}/>
                            ))
                        }
                    </div>
                )}
            </div>
        </div>
    )
}
export default FavoritesPage;
