import PageHeader from "../pageHeader/PageHeader";
import Banner from "../banner/Banner";
import imgBannerFlowers from "../../assets/images/img_banner_flowers.png";
import {useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {fetchProductsByCategory} from "../../store/productsSlice";
import ProductsList from "../../components/products/productsList/ProductList";

const MixedBloomsPage = () => {
    const dispatch = useDispatch();
    const { items, status, error } = useSelector(state => state.products.filtered);

    useEffect(()=>{
        dispatch(fetchProductsByCategory(5));

    },[dispatch]);
    console.log(items)

    if (status === "loading") return <p>Загрузка продуктов...</p>;
    if (status === "failed") return <p>Ошибка: {error}</p>;
    if (!items || items.length === 0) return <p>Нет товаров</p>;

    return(
        <>
            <PageHeader label="Mixed Blooms" subtitle="Colorful mixes of fresh, seasonal flowers." />
            <ProductsList  categoryId={4}/>
            <Banner background={imgBannerFlowers} badge="Custom Blooms" heading="Special Flowers for Your Special Moments" article="We craft unique flowers for your moments." btnBanner="Let's Create"/>
        </>
    )
}
export default MixedBloomsPage;
