import axios from "axios";
import {createAsyncThunk, createSlice} from "@reduxjs/toolkit";

export const fetchAllProducts = createAsyncThunk(
    'products/fetchAllProducts',
    async (_,{rejectWithValue}) => {

       try{const response = await axios.get("http://localhost:5050/api/products");
           return response.data;

       } catch(error){
           return rejectWithValue(error.response?.data?.message || error.message);
       }
    }
)

export const fetchProductById = createAsyncThunk(
    'products/fetchProductById',
    async (id,{rejectWithValue}) => {
        try{
            const response = await axios.get(`http://localhost:5050/api/products/${id}`);
            return response.data;
        }catch(error){
            return rejectWithValue(error.response?.data?.message || error.message);
        }
    }
)

export const fetchProductsByCategory = createAsyncThunk(
    'products/fetchProductsByCategory',
    async (_, {rejectWithValue}) => {
        try{
            const response = await axios.get(`http://localhost:5050/api/products/categories`);
            return response.data;
        }
        catch(error){
            return rejectWithValue(error.response?.data?.message || error.message);
        }
    }
)

const productsSlice = createSlice({
        name: "products",
        initialState: {
            items: [],
            FilteredProducts: {},
            selectedProduct: null,
            status: "idle",
            statusSelectedProduct: "idle",
            statusFilteredProduct: "idle",
            error: null,
            errorSelectedProduct: null,
            errorFilteredProduct: null,
        },
        reducers: {},
        extraReducers: (builder) => {
            builder
                .addCase(fetchAllProducts.pending, (state) => {
                    state.status = 'loading';
                })

                .addCase(fetchAllProducts.fulfilled, (state, action) => {
                    state.status = 'succeeded';
                    state.items = action.payload;
                })
                .addCase(fetchAllProducts.rejected, (state, action) => {
                    state.status = 'failed';
                    state.error = action.payload || action.error.message;
                })
                .addCase(fetchProductById.pending, (state) => {
                    state.statusSelectedProduct = 'loading';
                })
                .addCase(fetchProductById.fulfilled, (state, action) => {
                    state.statusSelectedProduct = 'succeeded';
                    state.selectedProduct = action.payload;
                })
                .addCase(fetchProductById.rejected, (state, action) => {
                    state.statusSelectedProduct = 'failed';
                    state.errorSelectedProduct = action.payload || action.error.message;
                })
                .addCase(fetchProductsByCategory.pending, (state, action) => {
                    state.statusFilteredProduct = 'loading';
                })
                .addCase(fetchProductsByCategory.fulfilled, (state, action) => {
                    state.statusFilteredProduct = 'succeeded';
                    state.FilteredProducts = action.payload;

                })
                .addCase(fetchProductsByCategory.rejected, (state, action) => {
                    state.statusFilteredProduct = 'failed';
                    state.errorFilteredProduct = action.payload || action.error.message;
                })

        }
    }
)

export default productsSlice.reducer;
